import { useState } from "react";
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {library} from "@fortawesome/fontawesome-svg-core";
import {faSearch} from "@fortawesome/free-solid-svg-icons";

import {Link} from "react-router-dom";

import { CircularProgressbar,buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

library.add(faSearch);

const Searchbar = styled.div`
  position: sticky;
  top: 50px;
  left: 0;
  right: 0;
  z-index: 1000;
  min-height: 50px;
  background: var(--bg);
  .container{
    input{
      width: 100%;
      padding: 6.5px 6.5px 6.5px 37.5px;
      background: var(--bg);
      border: 1px solid #495162;
      border-radius: 3px;
      outline: 0;
      color: #fff;
      transition: all 0.2s ease-in-out;
      &:focus {
        background: #fff;
        border: 1px solid red !important;
        color: var(--bg);
      }
    }
    .icon{
      position: absolute;
      top: 3px;
      left: 10px;
      color: var(--text);
      &.inputActive{
        color: var(--red);
      }
    }
  }
`;

const MovieList = styled.ul`
  display: flex;
  flex-wrap: wrap;
`;

const MovieItem = styled.li`
  width: 25%;
  padding: 10px;
  @media(max-width: 767px;){
    width: 50%;
  }
  .mvInfo{
    position: relative;
    padding: 10px 40px 8px 8px;
    .year{
      font-size: 11px;
      color: #fff;
    }
    .mvName{
      font-size: 17px;
      color: var(--red);
      font-weight: bold;
    }
    .rate{
      position: absolute;
      right: 0;
      top: 50%;
      transform: translateY(-50%);
      width: 26px;
      height: 26px;
    }
  }
`;

function Main() {

  const [searchText, setSearchText] = useState("");
  const [inputActive, setInputActive] = useState(false);

  const searchAction = (e) => {
    setSearchText(e.target.value);
  };

  const focusHandler = () => {
    setInputActive(!inputActive);
  }

  return (
  <>
    <Searchbar>
    <div className="container">
      <span className={inputActive ? "icon inputActive": "icon"}>
        <FontAwesomeIcon icon="search" />
      </span>        
      <input type="text" 
      value={searchText} 
      onFocus={focusHandler}
      onBlur={focusHandler}
      onChange={searchAction} 
      placeholder="영화를 검색하세요." />
    </div>
    </Searchbar>
    <div className="container">
      <MovieList>
        <MovieItem>
          <Link to="">
            <img src="https://via.placeholder.com/300x450" 
            alt="" 
            className="img-auto" 
            />
            <div className="mvInfo">
            <p className="year">2021-01-01</p>
            <p className="mvName">영화제목</p>
            <p className="rate">
              <CircularProgressbar value={60} text={`${60}%`} styles={buildStyles({
                pathColor: `#dc2d43`,
                textColor: "#dc2d43",
                trailColor: "#d6d6d6",
                backgroundColor: "#dc2d43",
              })} />
            </p>
            </div>
          </Link>
        </MovieItem>
        <MovieItem>
          <Link to="">
            <img src="https://via.placeholder.com/300x450" alt="" className="img-auto" />
            <div className="mvInfo">
            <p className="year">2021-01-01</p>
            <p className="mvName">영화제목</p>
            <p className="rate">평점</p>
            </div>
          </Link>
        </MovieItem>
        <MovieItem>
          <Link to="">
            <img src="https://via.placeholder.com/300x450" alt="" className="img-auto" />
            <div className="mvInfo">
            <p className="year">2021-01-01</p>
            <p className="mvName">영화제목</p>
            <p className="rate">평점</p>
            </div>
          </Link>
        </MovieItem>
        <MovieItem>
          <Link to="">
            <img src="https://via.placeholder.com/300x450" alt="" className="img-auto" />
            <div className="mvInfo">
            <p className="year">2021-01-01</p>
            <p className="mvName">영화제목</p>
            <p className="rate">평점</p>
            </div>
          </Link>
        </MovieItem>
        <MovieItem>
          <Link to="">
            <img src="https://via.placeholder.com/300x450" alt="" className="img-auto" />
            <div className="mvInfo">
            <p className="year">2021-01-01</p>
            <p className="mvName">영화제목</p>
            <p className="rate">평점</p>
            </div>
          </Link>
        </MovieItem>
      </MovieList>
    </div>
  </>
  );
}

export default Main;
