import styled from 'styled-components';
import {library} from "@fortawesome/fontawesome-svg-core";
import {faSortDown,faSearch} from "@fortawesome/free-solid-svg-icons";

import {Link} from 'react-router-dom';
import { useState } from 'react/cjs/react.development';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

library.add(faSearch, faSortDown);

const Navbar = styled.nav`
  position: sticky;
  top: 0;
  left: 0;
  right: 0;
  min-height: 50px;
  z-index: 1000;
  background: var(--bg);
  .logo{
    color: var(--red);
  }
  .container{
    display: flex;
    justify-content: space-between;
    min-height: 50px;
    align-items: center;
    .searchType{
      button{
        font-size: 13px;
        background: transparent;
        color: #fff;
        border: 0;
        outline: 0;
        cursor: pointer;
        span{
          margin-left: 10px;
        }
        transition: color 0.2s ease-in-out;
        &:first-child{
          margin-right: 10px
        }
        &:hover, &.on{
          color: var(--red);
        }
      }
    }
  }
`;

const Header = () => {

  const [listGenre, setListGenre] = useState([]);
  const [btn1,setBtn1] = useState(false)
  const [btn2,setBtn2] = useState(false)

  const handleBtn1 = () => {
    setBtn1(!btn1);
    setBtn2(false);
  }

  const handleBtn2 = () => {
    setBtn1(false);
    setBtn2(!btn2);
  }

  useState(() => {},[]);

  return (
        <Navbar>
      <div className="container">
        <Link to="/" className="logo">MOVIE DB</Link>
        <div className="searchType">
        <button type="button" className={btn1 ? "on" : ""} onClick={handleBtn1}>
          장르별
          <span>
          <FontAwesomeIcon icon="sort-down" />
          </span>
          </button>
        <button type="button" className={btn2 ? "on" : ""} onClick={handleBtn2}>
          연도별
          <span>
          <FontAwesomeIcon icon="sort-down" />
          </span>
          </button>
        </div>
      </div>
    </Navbar>
    );
}

export default Header;
