import {useEffect, useState} from 'react';
import instance from '../components/instance';
import styled from 'styled-components';
import {IMAGE_BASE_URL} from '../constants/constants';
import BgImage from './bgImage';
import moment from 'moment';

const UpperInfo = styled.div`
    position: relative;
    height: 300px;
`;

const BgImg = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
`;

const BackDropImg = styled.img`
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
`;

const MovieDetail = ({match}) => {

    const [backDropImg, setBackDropImg] = useState("");
    const [posterPath, setPosterPath] = useState("");
    const [title, setTitle] = useState("");
    const [relDate, setReldate] = useState("");

    const getMovieDetail = async () => {
        const result = await instance.get(`movie/${match.params.id}`);
        setBackDropImg(result.data.backdrop_path);
        setPosterPath(result.data.poster_path);
        setTitle(result.data.title);

        const mm = moment(result.data.release_date).format('YYYY');
        console.log(mm);
        setReldate(moment(result.data.release_date).format("YYYY"));
    };

    useEffect(() => {
        const { id } = match.params;
        if (id) getMovieDetail();
    }, []);

    return (
        <>
            <UpperInfo>
                <BackDropImg src={`${IMAGE_BASE_URL}w1280${backDropImg}` } />
                <BgImage imgSrc={backDropImg ? `${IMAGE_BASE_URL}w1280${backDropImg}` : `https://via.placeholder.com/1280x500`} />
                <div className="container">
                    <img src={`${IMAGE_BASE_URL}w1280${backDropImg}`} alt="" />
                    <h1>{title}</h1>
                    <p>{relDate}</p>
                </div>
            </UpperInfo>
        </>
    );
}

export default MovieDetail;
