import { useState } from 'react';
import styled from 'styled-components';

const Navbar = styled.nav`
  position: sticky;
  top: 0;
  left: 0;
  right: 0;
  min-height: 50px;
  background: var(--bg);
  .logo{
    color: var(--red);
  }
  .container{
    display: flex;
    justify-content: space-between;
    min-height: 50px;
    align-items: center;
  }
`;

const Searchbar = styled.div`
  position: sticky;
  top: 50px;
  left: 0;
  right: 0;
  min-height: 50px;
  background: var(--bg);
  .container{
    input{
      width: 100%;
      padding: 6.5px 6.5px 6.5px 37.5px;
      background: var(--bg);
      border: 1px solid #495162;
      border-radius: 3px;
      outline: 0;
      color: #fff;
      transition: all 0.2s ease-in-out;
      &:focus {
        background: #fff;
        border-color: var(--active);
        color: var(--bg);
      }
    }
  }
`;

function App() {

  const [searchText, setSearchText] = useState[''];

  return (
  <>
    <Navbar>
      <div className="container">
        <a href="/" className="logo">MOVIE DB</a>
        <div>
        <button>장르별</button>
        <button>연도별</button>
        </div>
      </div>
    </Navbar>
    <Searchbar>
    <div className="container">
      <input type="text" placeholder="영화를 검색하세요." />
    </div>
    </Searchbar>
  </>
  );
}

export default App;
