import {useEffect, useState} from 'react';
import instance from '../components/instance';
import styled from 'styled-components';
import {IMAGE_BASE_URL} from '../constants/constants';
import BgImage from './bgImage';

const UpperInfo = styled.div`
    position: relative;
    height: 300px;
    border: 1px solid #fff;
`;

const BgImg = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
`;

const BackDropImg = styled.img``;

const MovieDetail = ({match}) => {

    const [backDropImg, setBackDropImg] = useState('');

    const getMovieDetail = async () => {
        const result = await instance.get(`movie/${match.params.id}`);
        setBackDropImg(result.data.backdrop_path);
    };

    useEffect(() => {
        const { id } = match.params;
        if (id) getMovieDetail();
    }, []);

    return (
        <>
        <UpperInfo>
            <BgImage imgSrc={`${IMAGE_BASE_URL}w1280${backDropImg}`} />
            <BgImg style={{
                background: "url("+ IMAGE_BASE_URL + "w1280" + backDropImg + ")", 
                backgroundSize: "cover", 
                backgroundRepeat: "no-repeat", 
                backgroundPosition: "center",
            }}></BgImg>
            {/* <BackDropImg src={`${IMAGE_BASE_URL}w1280${backDropImg}`} alt='' /> */}
        </UpperInfo>
        </>
    );
}

export default MovieDetail;
